import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/services/authentication.service';
// Import any other services you need to fetch user data

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  currentUser: any = null;

  constructor(private authService: AuthService) {}

  ngOnInit() {
    if (this.authService.isAuthenticated()) {
      // Assuming you have a method to fetch user data
      // Replace 'getUserDetails' with the actual method you have
      this.getUserDetails().then(user => {
        this.currentUser = user;
      });
    }
  }

  getUserDetails() {
    // Implement this method to fetch user data from your backend or service
    // This is just a placeholder
    return Promise.resolve({
      UserPhoto: 'base64imageString', // Replace with actual logic to get user photo
      // ... other user details
    });
  }

  get isAuthenticated(): boolean {
    return this.authService.isAuthenticated();
  }

  // ... rest of your component code ...
}
