import {Component, Output, EventEmitter, Input} from '@angular/core';
import {IThread} from "../thread/thread";

@Component({
  selector: 'app-comment-box',
  templateUrl: './comment-box.component.html',
  styleUrls: ['./comment-box.component.css']
})
export class CommentBoxComponent {
  newCommentContent = '';
  @Input() thread?: IThread;

  @Output() addComment = new EventEmitter<string>();

  onAddComment() {
    this.addComment.emit(this.newCommentContent);
    this.newCommentContent = ''; // Reset the input
  }
}
<div class="comment-box">
  <textarea [(ngModel)]="newCommentContent" placeholder="Write a comment..."></textarea>
  <button (click)="onAddComment()">Add Comment</button>
</div>
