import { Component, Input, OnInit } from '@angular/core';
import { IComment } from './comment';
import { UserService } from './user';

@Component({
  selector: 'app-comment-list',
  templateUrl: './comment-list.component.html',
  styleUrls: ['./comment-list.component.css']
})
export class CommentListComponent implements OnInit {
  @Input() comment!: IComment;

  constructor(private userService: UserService) {}

  getAuthorName(userId: number): string {
    // Logic to fetch the user's name based on the user ID
    // This can be a call to the UserService to fetch the user data
    // For simplicity, let's assume userService has a method to get the user name
    return this.userService.getUserNameById(userId);
  }

  // Rest of your component code...
}
