import { Component, Input, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { IComment } from './comment';
import { UserService } from './user.service'; // Ensure the path is correct

@Component({
  selector: 'app-comment-list',
  templateUrl: './comment-list.component.html',
  styleUrls: ['./comment-list.component.css']
})
export class CommentListComponent {
  @Input() comment!: IComment;

  constructor(private userService: UserService) {}

  getAuthorName(userId: number): Observable<string> | Promise<string> {
    // This should return an Observable or a Promise
    return this.userService.getUserNameById(userId);
  }
}
