import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from 'src/app/services/authentication.service';

@Component({
  selector: 'app-user-settings',
  templateUrl: './user-settings.component.html',
  styleUrls: ['./user-settings.component.css']
})
export class UserSettingsComponent implements OnInit {
  currentUser: any = {
    email: '',
    username: '',
    id: null,
  };
  settingsForm!: FormGroup;
  loading = false;
  errorMessage: string = '';
  successMessage: string = '';
  selectedFile: File | undefined;
  imagePreviewUrl: string | null = null;

  constructor(
    private authService: AuthService,
    private formBuilder: FormBuilder
  ) {}

  ngOnInit() {
    this.initializeForm();
    this.loadCurrentUser();
  }

  private loadCurrentUser() {
    const userId = localStorage.getItem('userId');
    if (userId) {
      this.authService.getUserDetails(+userId).subscribe({
        next: (user) => {
          this.currentUser = user;
          this.updateFormValues(user);
        },
        error: (err) => {
          console.error('Error fetching user details:', err);
        }
      });
    } else {
      console.error('User ID not found in local storage');
    }
  }

  private initializeForm() {
    this.settingsForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      username: ['', Validators.required],
      password: ['', [Validators.minLength(8)]]
    });
  }

  private updateFormValues(user: any) {
    this.settingsForm.patchValue({
      email: user.email,
      username: user.username
    });
  }

  onUpdateSettings() {
    if (this.settingsForm.valid) {
      this.loading = true;

      const formData = new FormData();
      formData.append('id', this.currentUser.id.toString());
      formData.append('email', this.settingsForm.value.email);
      formData.append('username', this.settingsForm.value.username);

      // Append password if it's not empty
      if (this.settingsForm.value.password) {
        formData.append('password', this.settingsForm.value.password);
      }

      // Append the profile picture if it's selected
      if (this.selectedFile) {
        formData.append('profilePicture', this.selectedFile, this.selectedFile.name);
      }

      this.authService.updateUserDetails(this.currentUser.id, formData).subscribe({
        next: (response) => {
          console.log('User settings updated', response);
          this.loading = false;
          this.successMessage = 'Update successful!';
          // Update currentUser object
          this.currentUser = {
            ...this.currentUser,
            email: this.settingsForm.value.email,
            username: this.settingsForm.value.username
          };
          // Reset the selected file
          this.selectedFile = undefined;
          this.imagePreviewUrl = null;
        },
        error: (err) => {
          this.errorMessage = 'Error updating user settings';
          console.error('Error updating user settings:', err);
          this.loading = false;
        }
      });
    }
  }

  handleFileInput(event: Event): void {
    const element = event.target as HTMLInputElement;
    const file = element.files ? element.files[0] : null;
    if (file) {
      this.selectedFile = file;
      // Create a URL for the selected file for preview
      const fileReader = new FileReader();
      fileReader.onload = () => {
        if (fileReader.result) {
          this.imagePreviewUrl = fileReader.result as string;
        }
      };
      fileReader.readAsDataURL(file);
    }
  }



  get isAuthenticated(): boolean {
    return this.authService.isAuthenticated();
  }

  logout() {
    this.authService.logout();
  }
}
