import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private baseUrl = 'http://localhost:5186/api/authenticate';

  constructor(private http: HttpClient, private router: Router) { }

  login(email: string, password: string): Observable<any> {
    return this.http.post(`${this.baseUrl}/login`, { email, password });
  }

  register(registerData: any): Observable<any> {
    return this.http.post(`${this.baseUrl}/register`, registerData);
  }

  checkUsernameAvailability(username: string): Observable<boolean> {
    return this.http.get<boolean>('/api/user/check-username', { params: { username } });
  }

  // Store authentication token
  storeAuthToken(token: string): void {
    localStorage.setItem('authToken', token);
  }

  // Check if the user is authenticated
  isAuthenticated(): boolean {
    const token = localStorage.getItem('authToken');
    // Implement logic here to check if the token is valid (not expired)
    // For simplicity, we'll just check if the token exists
    return !!token;
  }

  // Logout the user
  logout(): void {
    localStorage.removeItem('authToken');
    this.router.navigate(['/']);
  }
}
