import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-registration',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegistrationComponent {
  email: string = '';
  password: string = '';

  constructor(private http: HttpClient) {}

  onSubmit() {
    const registrationData = { email: this.email, password: this.password };
    this.http.post('/api/register', registrationData).subscribe(
      (response) => {
        // Handle successful registration, e.g., show a success message or redirect.
      },
      (error) => {
        // Handle registration error, e.g., display an error message.
      }
    );
  }
}

<div class="container">
  <div class="row justify-content-center">
    <div class="col-md-6">
      <div class="card">
        <div class="card-header">Login</div>
        <div class="card-body">
          <form (ngSubmit)="onSubmit()">
            <div class="form-group">
              <label for="email">Email</label>
              <input type="email" id="email" [(ngModel)]="email" name="email" required>
            </div>
            <div class="form-group">
              <label for="password">Password</label>
              <input type="password" id="password" name="password" [(ngModel)]="password" class="form-control" required />
            </div>
            <button type="submit">Register</button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
