import { Component } from '@angular/core';
import { Router } from '@angular/router';
import {FormBuilder, FormGroup, Validators, AsyncValidatorFn, AbstractControl, ValidationErrors} from '@angular/forms';
import { AuthService } from 'src/app/services/authentication.service';
import { Observable, of } from 'rxjs';
import { map, catchError, debounceTime } from 'rxjs/operators';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  registerForm!: FormGroup;

  constructor(
    private authService: AuthService,
    private router: Router,
    private formBuilder: FormBuilder
  ) {}

  ngOnInit() {
    this.registerForm = this.formBuilder.group({
      username: ['', [Validators.required], [this.usernameValidator()]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(8)]],
      confirmPassword: ['', [Validators.required]]
    }, { validator: this.matchPassword });
  }

  matchPassword(group: FormGroup): ValidationErrors | null {
    const passwordControl = group.get('password');
    const confirmPasswordControl = group.get('confirmPassword');

    if (!passwordControl || !confirmPasswordControl) {
      return { invalidForm: true };
    }

    const password = passwordControl.value;
    const confirmPassword = confirmPasswordControl.value;

    return password === confirmPassword ? null : { notMatching: true };
  }

  usernameValidator(): AsyncValidatorFn {
    return (control: AbstractControl): Observable<ValidationErrors | null> => {
      if (!control.value) {
        return of(null);
      }
      return this.authService.checkUsernameAvailability(control.value).pipe(
        debounceTime(500),
        map(isAvailable => (isAvailable ? null : { alreadyTaken: true })),
        catchError(() => of(null))
      );
    };
  }


  onRegister() {
    if (this.registerForm.valid) {
      const registerData = this.registerForm.value;
      this.authService.register(registerData).subscribe({
        next: (data) => {
          this.authService.login(registerData.email, registerData.password).subscribe({
            next: (loginData) => {
              if (loginData && loginData.token) {
                this.authService.storeAuthToken(loginData.token);
                localStorage.setItem('userId', loginData.userId.toString());
                this.router.navigate(['/']);
              }
            },
            error: (loginError) => console.error('Login failed after registration', loginError)
          });
        },
        error: (registerError) => console.error('Registration failed', registerError)
      });
    }
  }

}
<!-- MAIN CONTENT -->
<div class="banner clearfix container-fluid">
  <!-- START OF BANNER -->
  <div class="background-light card flex-1 row" id="banner">
    <!--Welcome message-->
    <div class="col-md-6 banner-message">
      <p class="header-font">
        Welcome to the Beverage Goblins online community.<br>
        A place to discuss all things related to drinks with other people that share your enthusiasm for beverages.
      </p>
    </div>
    <!-- Breadcrumbs -->
    <div class="background-dark card-footer justify-content-start">
      <div class="col-md">
        <nav aria-label="breadcrumb">
          <ol class="breadcrumb">
            <li class="breadcrumb-item active" aria-current="page">Board Index</li>
          </ol>
        </nav>
      </div>
    </div>
  </div>
</div>

<div style="display: flex; justify-content: center; align-items: center; min-height: 30vh; padding: 20px;">
  <section class="register-card" style="max-width: 2000px; margin: auto; width: 100%;">
  <div class="row d-flex justify-content-center col-12 mb-5">
    <div class="col-5 border-pink rounded m-20 background-light card pb-2">

      <form [formGroup]="registerForm" (ngSubmit)="onRegister()">

        <div class="d-flex justify-content-center">
          <h1 class="h3 mb-3 mt-4 std-color">Create a new account</h1>
        </div>

        <hr />

        <!-- Username -->
        <div class="form-floating mb-2">
          <input type="text" class="form-control custom-form-control" formControlName="username">
          <label class="form-label custom-form-label"><i class="fa-regular fa-user"></i>Username</label>
          <div *ngIf="registerForm.get('username')?.touched">
            <small class="text-danger" *ngIf="registerForm.get('username')?.errors?.required">Username is required.</small>
            <small class="text-danger" *ngIf="registerForm.get('username')?.errors?.alreadyTaken">Username is already taken.</small>
          </div>
        </div>

        <!-- Email -->
        <div class="form-floating mb-2">
          <input type="email" class="form-control custom-form-control" formControlName="email">
          <label class="form-label custom-form-label"><i class="fa-regular fa-envelope"></i>E-mail</label>
          <div *ngIf="registerForm.get('email')?.touched">
            <small class="text-danger" *ngIf="registerForm.get('email')?.errors?.required">Email is required.</small>
            <small class="text-danger" *ngIf="registerForm.get('email')?.errors?.email">Invalid email format.</small>
          </div>
        </div>

        <!-- Password -->
        <div class="form-floating mb-2">
          <input type="password" class="form-control custom-form-control" formControlName="password">
          <label class="form-label custom-form-label"><i class="fa-solid fa-key"></i>Password</label>
          <div *ngIf="registerForm.get('password')?.touched">
            <small class="text-danger" *ngIf="registerForm.get('password')?.errors?.required">Password is required.</small>
            <small class="text-danger" *ngIf="registerForm.get('password')?.errors?.minlength">Password must be at least 6 characters long.</small>
          </div>
        </div>

        <!-- Confirm Password -->
        <div class="form-floating mb-2">
          <input type="password" class="form-control custom-form-control" formControlName="confirmPassword">
          <label class="form-label custom-form-label"><i class="fa-solid fa-key"></i>Repeat password</label>
          <div *ngIf="registerForm.get('confirmPassword')?.touched">
            <small class="text-danger" *ngIf="registerForm.get('confirmPassword')?.errors?.required">Confirm password is required.</small>
            <small class="text-danger" *ngIf="registerForm.errors?.notMatching"> Passwords do not match.</small>
          </div>
        </div>

        <div class="d-flex">
          <button type="submit" class="mb-5 login-pink-btn m-auto" [disabled]="!registerForm.valid">Register</button>
        </div>

      </form>

    </div>
  </div>
  </section>
</div>
